package com.java.onmobile;

public class ConcatInteger {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(concat(12, 8));
	}
	
	public static Integer concat(Integer valueA, Integer valueB)
	{
		return valueA+valueB;
	}

	public static String concat(String valueA, String valueB)
	{
		return valueA+valueB;
	}
	public static Long concat(Long valueA, Long valueB)
	{
		return valueA+valueB;
	}
	public static Float concat(Float valueA, Float valueB)
	{
		return valueA+valueB;
	}
	
	public static Double concat(Double valueA, Double valueB)
	{
		return valueA+valueB;
	}
	
}
